CREATE SEQUENCE uni_seq
       INCREMENT BY 1
       START WITH 1
       MINVALUE 1
       MAXVALUE 10000
       NOCYCLE
       NOCACHE
       NOORDER;

CREATE TABLE uni_type(
   uni_idx                   NUMBER(5,0) NOT NULL PRIMARY KEY
  ,uni_name                  VARCHAR2(100) NOT NULL
  ,uni_branch                VARCHAR2(100) NOT NULL
  ,uni_region                VARCHAR2(100) NOT NULL
  ,uni_recruitment           VARCHAR2(100) NOT NULL
  ,uni_department            VARCHAR2(100) NOT NULL
  ,uni_selection             VARCHAR2(100) NOT NULL
  ,uni_selection_type        VARCHAR2(100) NOT NULL
  ,uni_selection_name        VARCHAR2(100) NOT NULL
  ,uni_type_number           NUMBER(4,0)
  ,uni_2022_competition_rate NUMBER(4,2)
  ,uni_2022_rate             NUMBER(4,2)
);


SELECT * FROM UNI_TYPE;

CREATE TABLE UNI_MEMBER(
    UM_ID VARCHAR2(30) NOT NULL PRIMARY KEY,
    UM_PASSWORD VARCHAR2(30) NOT NULL,
    UM_NAME VARCHAR2(30) NOT NULL,
    UM_DATE DATE,
    UM_APIKEY VARCHAR2(100),
    UM_KEYDATE DATE
);
